import './App.css';
import {useState} from "react";
import logo from "./assets/festivida.png";
import ClientDetails from './components/ClientDetails';
import Dates from './components/Dates';
import Footer from './components/Footer';
import Header from './components/Header';
import MainDetails from './components/MainDetails';
import Notes from './components/Notes';
import Table from './components/Table';

function App() {

  const [showInvoice, setShowInvoice] = useState(false)
  const handlePrint = () => {
    window.print()
  }


  const [name, setName] = useState("")
  const [address, setAddress] = useState("")
  const [email, setEmail] = useState("")
  const [phone, setPhone] = useState("")
  const [bankname, setBankName] = useState("")
  const [website, setWebsite] = useState("")
  const [bankaccountnumber, setBankAccountNumber] = useState("")
  const [clientname, setClientName] = useState("")
  const [clientaddress, setClientAddress] = useState("")
  const [invoicenumber, setInvoiceNumber] = useState("")
  const [invoicedate, setInvoiceDate] = useState("")
  const [duedate, setDueDate] = useState("")
  const [notes, setNotes] = useState("")
  const [file, setFile] = useState();


    function handleChange(e) {
        console.log(e.target.files);
        setFile(URL.createObjectURL(e.target.files[0]));
    }


  return (
   <main className='m-5 p-5 md:max-w-xl md:mx-auto lg:max-w-2xl xl:max-w-4xl xl:mx-auto bg-white rounded shadow' > 

      {showInvoice ? <div>
      <Header handlePrint={handlePrint} logo= {logo}/>

      <MainDetails name= {name} address = {address} file= {file}/>


      <ClientDetails clientname = {clientname} clientaddress = {clientaddress}/>


      <Dates invoicenumber = {invoicenumber} invoicedate = {invoicedate} duedate = {duedate}/>

      <Table/>


      <Notes notes = {notes}/>
      <Footer name={name} address= {address} website = {website} email = {email} phone = {phone} bankname = {bankname} bankaccountnumber= {bankaccountnumber}  clientname = {clientname} />

      <button onClick = {()=> setShowInvoice(false)} className='bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300 mt-5'>Edit Information</button>
      </div>: (
        <>
           {/* name, address, email, phone, bankname, website, bankaccountnumber, clientname, clientaddress,
        invoicenumber, invoicedate, duedate, notes */}

       <div className='flex flex-col justify-center'> 

        <article className="md:grid grid-cols-2 gap-10">

          <div className="flex flex-col"> 
          <label htmlFor="name"> Enter your name</label>
          <input type="text" name = "text" id = "name" placeholder='Enter your name' autoComplete='off'
          value={name} onChange={(e) => setName(e.target.value)}/>

          </div>
          <div className="flex flex-col"> </div>
          <label htmlFor="address"> Enter your address</label>
          <input type="text" name = "text" id = "address" placeholder='Enter your address' autoComplete='off'
          value={address} onChange={(e) => setAddress(e.target.value)}/>

        </article>


        <label htmlFor="email"> Enter your email</label>
        <input type="email" name = "email" id = "email" placeholder='Enter your email' autoComplete='off'
        value={email} onChange={(e) => setEmail(e.target.value)}/>

        <label htmlFor="phone"> Enter your phone</label>
        <input type="text" name = "text" id = "phone" placeholder='Enter your phone' autoComplete='off'
        value={phone} onChange={(e) => setPhone(e.target.value)}/>

        <label htmlFor="bankname"> Enter your Bank Name</label>
        <input type="text" name = "text" id = "bankname" placeholder='Enter your Bank Name' autoComplete='off'
        value={bankname} onChange={(e) => setBankName(e.target.value)}/>

        <label htmlFor="website"> Enter your website</label>
        <input type="url" name = "website" id = "website" placeholder='Enter your Website' autoComplete='off'
        value={website} onChange={(e) => setWebsite(e.target.value)}/>

        <label htmlFor="bankaccountnumber"> Enter your bank account number</label>
        <input type="text" name = "text" id = "bankaccountnumber" placeholder='Enter your Bank Account Number' autoComplete='off'
        value={bankaccountnumber} onChange={(e) => setBankAccountNumber(e.target.value)}/>

        <label htmlFor="clientname"> Enter your Client Name</label>
        <input type="text" name = "text" id = "clientname" placeholder='Enter your clientname' autoComplete='off'
        value={clientname} onChange={(e) => setClientName (e.target.value)}/>

        <label htmlFor="clientaddress"> Enter your clientaddress</label>
        <input type="text" name = "clientaddress" id = "clientaddress" placeholder='Enter your name' autoComplete='off'
        value={clientaddress} onChange={(e) => setClientAddress(e.target.value)}/>

        <label htmlFor="invoicenumber"> Enter your invoice number</label>
        <input type="number" name = "text" id = "invoicenumber" placeholder='Enter your invoicenumber' autoComplete='off'
        value={invoicenumber} onChange={(e) => setInvoiceNumber(e.target.value)}/>

        <label htmlFor="invoicedate"> Enter your invoicedate</label>
        <input type="date" name = "text" id = "invoicedate" placeholder='Enter your invoicedate' autoComplete='off'
        value={invoicedate} onChange={(e) => setInvoiceDate(e.target.value)}/>

        <label htmlFor="duedate"> Enter your duedate</label>
        <input type="date" name = "text" id = "duedate" placeholder='Enter your duedate' autoComplete='off'
        value={duedate} onChange={(e) => setDueDate(e.target.value)}/>

        <label htmlFor="notes"> Enter your notes</label>
        <input type="notes" cols = "30" rows = "10" name = "text" id = "notes" placeholder='Aditional notes to the client' autoComplete='off'
        value={notes} onChange={(e) => setNotes(e.target.value)}/>

        <label htmlFor="image"> Enter your image</label>
        <input type="file" onChange={handleChange} />
            <img src={file} />


        <button onClick= {(e)=> setShowInvoice(true)}className='bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300'>Preview Invoice</button>
       </div>
        </>)
      }
   </main>);
}

export default App;
