import './App.css';

function App() {

  const handlePrint = () => {
    window.print();
  };

  return (
   <main className='m-5 p-5 xl:max-w-4xl xl:mx-auto bg-white rounded shadow' > 
      <header className='flex flex-col items-center justify-center mb-5 xl:flex-row xl:justify-between'>
        <div>
          <h1 className='font-bold uppercase tracking-wide text-4xl mb-3'>Invoicer</h1>
        </div>

        <div>
          <ul className='flex items-center justify-between flex-wrap'>
            <li><button onClick={handlePrint} className='btn btn-print'>Print</button></li>
            <li className='btn btn-download' >download</li>
            <li className='btn btn-send'>Send</li>
          </ul>
        </div>
      </header>

      <section className='flex flex-col items-end justify-end'>
        <h2 className='text-xl uppercase'>thomas edison</h2>
        <p>your address</p>
        {/* <input type="text"  name='text' id='text' placeholder='Enter your name' required/>
        <input type="text"  name='text' id='text' placeholder='Enter your name' required/>
        <input type="text"  name='text' id='text' placeholder='Enter your name' required/> */}

      </section>

         {/* Client detail */}
      <section className='mt-5'>
        <h2 className='text-xl uppercase'>Client name</h2>
        <p>Clients address</p>
      </section>
      {/* dates */}

      <article className='my-5 flex  items-end justify-end'>
        <ul>
          <li><span className='font-bold'>Invoiver number:</span></li>
          <li><span className='font-bold'>Invoive Data:</span></li>
          <li><span className='font-bold'>Due Date:</span></li>
        </ul>
      </article>
          <div className='my-5'>this is the table</div>

      {/* end of notes, footer, End of footer */}

   </main>
  );
}

export default App;
